package day2;

public class AssignmentAllPrimitiveDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte num=10;
		short number=100;
		int value=5000;
		long Mob=8976432156L;
		float Height=(float)150.3;
		double val=1234.456789;
		char alpha='A';
		boolean Single=true;
		System.out.println("Byte value:"+num);
		System.out.println("Short value:"+number);
		System.out.println("Int value:"+value);
		System.out.println("Long value:"+Mob);
		System.out.println("Float value:"+Height);
		System.out.println("Double value:"+val);
		System.out.println("Char value:"+alpha);
		System.out.println("Boolean value:"+Single);

	}

}
